INSERT INTO `about` (`id`, `bg_image`, `title`, `excerpt`, `first_content`, `first_image`, `first_alt_image`, `first_title_image`, `second_image`, `second_alt_image`, `second_title_image`, `second_content`, `created_at`, `updated_at`) VALUES
(1, 'about\\January2022\\3TRilCOicuDuyTi5hTgu.jpg', 'О Союзе IT Prom', 'Расширяя границы возможного', '<p>Союз IT Prom был основан в 2020 году.<br /><br />Основной целью Союза IT Prom является <strong>объединение отечественных игроков информационно-коммуникационных технологий</strong> (далее &ndash; ИКТ) для консолидированного решения задач развития и продвижения отрасли.<br />&nbsp;&nbsp; &nbsp;<br />Союз IT Prom объединяет отечественные компании в сфере <strong>услуг инфокоммуникаций, сертификации, производства компьютерной техники, серверов, телекоммуникационного, сетевого оборудования, центров обработки данных и производства специального оборудования</strong>.<br /><br />Действующие члены Союза IT Prom: <strong>АО &laquo;Логиком&raquo;, ТОО &laquo;ICS Holding Qazaqstan&raquo;, ТОО &laquo;Agile Technologies&raquo;, АО &laquo;Казахтелеком&raquo;, АО &laquo;Kcell&raquo;, ТОО &laquo;ARLAN SI&raquo;, ТОО &laquo;Infratech&raquo;, ТОО &laquo;QazCloud&raquo;</strong>.<br /><br />Союз IT Prom аккредитован в <strong>Министерстве цифрового развития, инноваций и аэрокосмической промышленности Республики Казахстан</strong>. <br /><br />В стратегическом аспекте Союз IT Prom занимается решениями вопросов <strong>системных барьеров в сфере технического регулирования, стандартизации, закупок в государственном и в квазигосударственном секторах, закупках крупных недропользователей, получении мер государственной финансовой поддержки и кадрового обеспечения</strong>.<br /><br />Среди оперативных задач Союза IT Prom проекты по <strong>маркировке товаров ИКТ</strong>, проекты по <strong>производству смартфонов</strong>, <strong>видеокамер</strong>, <strong>серверов</strong>, организации <strong>унифицированного рабочего места</strong>, создании <strong>единой электронной платформы</strong> участков рынка ИКТ, содействие в создании полноценной системы утилизации продукции ИКТ, разработка<strong> 5G/Open-RAN</strong> решений в сфере недропользования, оказание содействия отечественным предприятиям в процессе<strong> встраивания в глобальные цепочки создания стоимостей</strong>. <br /><br /></p>', 'about\\February2022\\pMWZwIY3UuKZLJD2lsl2.png', 'shutterstock', 'Credit: Pexels.com', 'about\\February2022\\n7CLQcWuy2nJsjAdggDO.png', 'iStock', 'Credit: iStock', '<p>Приоритетной задачей Союза IT Prom является выступление в роли <strong>института коллаборации и партнера Правительства страны</strong> в формировании единой диалоговой площадки государства и бизнеса, способную стать движущей силой цифровой экосистемы промышленности Казахстана.<br /><br /></p>', '2022-01-25 05:58:00', '2022-02-14 08:21:49');
